mapper.select:
select 

from yl_role
where ROLE_CODE = #{roleCode,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=INTEGER}

mapper.delete:
delete from yl_role
where ROLE_CODE = #{roleCode,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=INTEGER}

mapper.insert:
insert into yl_role (ROLE_CODE, ROLE_ID, ROLE_NAME, 
  ROLE_DESC, STATUS, CREATE_ACCOUNT, 
  CREATE_TIME, UPDATE_ACCOUNT, UPDATE_TIME
  )
values (#{roleCode,jdbcType=VARCHAR}, #{roleId,jdbcType=INTEGER}, #{roleName,jdbcType=VARCHAR}, 
  #{roleDesc,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{createAccount,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateAccount,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update yl_role
set ROLE_NAME = #{roleName,jdbcType=VARCHAR},
  ROLE_DESC = #{roleDesc,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=INTEGER},
  CREATE_ACCOUNT = #{createAccount,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  UPDATE_ACCOUNT = #{updateAccount,jdbcType=VARCHAR},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}
where ROLE_CODE = #{roleCode,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=INTEGER}
